mapper.select:

select ac_num, ac_ac_name, me_name, pr_name, pr_startprice, ac_startDate, ac_state, ar_bidprice

from auction join member on ac_me_id = me_id join product on pr_code = ac_pr_code

join auction_record on ac_num = ar_ac_num

where (pr_name like concat('%', #{criteria.search}, '%') or me_name like concat('%', #{criteria.search}, '%'))

and ac_startDate between #{criteria.fromDate} and #{criteria.toDate}

order by ac_num asc limit #{criteria.pageStart}, #{criteria.perPageNum}


select ac_num, pr_name, pr_startprice, ac_startdate, ac_finaldate, ac_state, ac_immediate

from auction join product on ac_pr_code = pr_code

where (pr_name like concat('%', #{criteria.search}, '%'))

and ac_startDate between #{criteria.fromDate} and #{criteria.toDate}

 order by ac_num asc limit #{criteria.pageStart}, #{criteria.perPageNum}

mapper.insert:

insert into auction_cancel(ac_num, ac_date, ac_reason, ac_ac_num)

select ifnull(max(ac_num), 0) + 1, now(), #{auctionCancle.ac_reason}, #{auctionCancle.ac_ac_num}

from auction_cancel

mapper.select:

select pr_name, me_name, ar_bidprice, re_progress, ac_deliveryway, de_state, de_startdate, de_completedate, or_num

from product join auction on pr_code = ac_pr_code join auction_record on ac_num = ar_ac_num join member on ac_me_id = me_id 

join `order` on me_id = or_me_id left join refund on or_num = re_or_num join delivery on or_num = de_or_num

where (pr_name like concat('%', #{criteria.search}, '%') or me_name like concat('%', #{criteria.search}, '%'))

and ac_startDate between #{criteria.fromDate} and #{criteria.toDate}

 order by ac_num asc limit #{criteria.pageStart}, #{criteria.perPageNum}

mapper.insert:

insert into refund(re_num, re_applydate, re_reason, re_or_num)

select ifnull(max(re_num), 0) + 1, now(), #{refund.re_reason}, #{refund.re_or_num}

from refund

mapper.select:

select pr_name, re_progress, re_applydate, re_confirmdate

from product join auction on pr_code = ac_pr_code join `order` on ac_num = or_ac_num left join refund on or_num = re_or_num

where (pr_name like concat('%', #{criteria.search}, '%'))

and ac_startDate between #{criteria.fromDate} and #{criteria.toDate}

 order by pr_name asc limit #{criteria.pageStart}, #{criteria.perPageNum}


select * from report join report_category on re_rc_num = rc_num left join product on re_report_product = pr_code

where re_me_id = #{user.me_id}

 order by re_num asc limit #{criteria.pageStart}, #{criteria.perPageNum}

mapper.update:

update member set me_pw = #{member.me_pw}, me_email = #{member.me_email}, me_phone = #{member.me_phone}, me_post_num = #{member.me_post_num}

, me_road_name = #{member.me_road_name}, me_detail_address = #{member.me_detail_address}

where me_id = #{member.me_id}
